import React, { useState, useEffect } from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  Container,
  Grid,
  Card,
  CardContent,
  Box,
  Chip,
  Alert,
  CircularProgress
} from '@mui/material';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  BarChart,
  Bar,
  PieChart,
  Pie,
  Cell
} from 'recharts';
import axios from 'axios';

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];

function App() {
  const [services, setServices] = useState([]);
  const [anomalies, setAnomalies] = useState([]);
  const [metrics, setMetrics] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, 30000); // Refresh every 30 seconds
    return () => clearInterval(interval);
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      setError(null);
      
      // Fetch service health with error handling
      let userServiceStatus = 'unavailable';
      let orderServiceStatus = 'unavailable';
      
      try {
        const userServiceHealth = await axios.get('http://localhost:8081/health', { timeout: 5000 });
        userServiceStatus = userServiceHealth.data.includes('healthy') ? 'healthy' : 'unhealthy';
      } catch (err) {
        userServiceStatus = 'unavailable';
      }
      
      try {
        const orderServiceHealth = await axios.get('http://localhost:8082/health', { timeout: 5000 });
        orderServiceStatus = orderServiceHealth.data.includes('healthy') ? 'healthy' : 'unhealthy';
      } catch (err) {
        orderServiceStatus = 'unavailable';
      }
      
      const servicesData = [
        {
          name: 'User Service',
          status: userServiceStatus,
          port: 8081,
          url: 'http://localhost:8081'
        },
        {
          name: 'Order Service',
          status: orderServiceStatus,
          port: 8082,
          url: 'http://localhost:8082'
        }
      ];
      
      setServices(servicesData);
      
      // Simulate metrics data (in real implementation, fetch from Prometheus)
      const mockMetrics = {
        cpuUsage: [
          { time: '10:00', user: 45, order: 52 },
          { time: '10:05', user: 48, order: 55 },
          { time: '10:10', user: 42, order: 48 },
          { time: '10:15', user: 50, order: 60 },
          { time: '10:20', user: 47, order: 53 }
        ],
        memoryUsage: [
          { time: '10:00', user: 256, order: 312 },
          { time: '10:05', user: 268, order: 325 },
          { time: '10:10', user: 245, order: 298 },
          { time: '10:15', user: 275, order: 340 },
          { time: '10:20', user: 260, order: 315 }
        ],
        responseTime: [
          { time: '10:00', user: 120, order: 95 },
          { time: '10:05', user: 135, order: 110 },
          { time: '10:10', user: 98, order: 85 },
          { time: '10:15', user: 150, order: 125 },
          { time: '10:20', user: 115, order: 100 }
        ]
      };
      
      setMetrics(mockMetrics);
      
      // Simulate anomaly data
      const mockAnomalies = [
        {
          id: 1,
          service: 'User Service',
          type: 'High CPU Usage',
          severity: 'warning',
          timestamp: new Date().toISOString(),
          description: 'CPU usage exceeded 80% for 5 minutes'
        },
        {
          id: 2,
          service: 'Order Service',
          type: 'Memory Leak',
          severity: 'critical',
          timestamp: new Date(Date.now() - 300000).toISOString(),
          description: 'Memory usage continuously increasing'
        }
      ];
      
      setAnomalies(mockAnomalies);
      
      setLoading(false);
    } catch (err) {
      // Only show error if it's not a network error (which we handle gracefully above)
      if (!err.message.includes('Network Error') && !err.message.includes('timeout')) {
        setError('Failed to fetch data: ' + err.message);
      }
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'healthy': return 'success';
      case 'unhealthy': return 'error';
      default: return 'default';
    }
  };

  const getSeverityColor = (severity) => {
    switch (severity) {
      case 'critical': return 'error';
      case 'warning': return 'warning';
      case 'info': return 'info';
      default: return 'default';
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="100vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <div className="App">
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Cloud Native Observability Dashboard
          </Typography>
        </Toolbar>
      </AppBar>

      <Container maxWidth="xl" sx={{ mt: 4, mb: 4 }}>
        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        <Grid container spacing={3}>
          {/* Service Status */}
          <Grid item xs={12}>
            <Typography variant="h5" gutterBottom>
              Service Status
            </Typography>
          </Grid>
          
          {services.map((service) => (
            <Grid item xs={12} md={6} key={service.name}>
              <Card>
                <CardContent>
                  <Box display="flex" justifyContent="space-between" alignItems="center">
                    <Typography variant="h6">{service.name}</Typography>
                    <Chip 
                      label={service.status} 
                      color={getStatusColor(service.status)}
                      variant="outlined"
                    />
                  </Box>
                  <Typography variant="body2" color="text.secondary">
                    Port: {service.port}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}

          {/* Metrics Charts */}
          <Grid item xs={12}>
            <Typography variant="h5" gutterBottom sx={{ mt: 3 }}>
              Performance Metrics
            </Typography>
          </Grid>

          <Grid item xs={12} md={4}>
            <Card>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  CPU Usage (%)
                </Typography>
                <ResponsiveContainer width="100%" height={200}>
                  <LineChart data={metrics.cpuUsage}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="time" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Line type="monotone" dataKey="user" stroke="#8884d8" name="User Service" />
                    <Line type="monotone" dataKey="order" stroke="#82ca9d" name="Order Service" />
                  </LineChart>
                </ResponsiveContainer>
              </CardContent>
            </Card>
          </Grid>

          <Grid item xs={12} md={4}>
            <Card>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  Memory Usage (MB)
                </Typography>
                <ResponsiveContainer width="100%" height={200}>
                  <LineChart data={metrics.memoryUsage}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="time" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Line type="monotone" dataKey="user" stroke="#8884d8" name="User Service" />
                    <Line type="monotone" dataKey="order" stroke="#82ca9d" name="Order Service" />
                  </LineChart>
                </ResponsiveContainer>
              </CardContent>
            </Card>
          </Grid>

          <Grid item xs={12} md={4}>
            <Card>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  Response Time (ms)
                </Typography>
                <ResponsiveContainer width="100%" height={200}>
                  <LineChart data={metrics.responseTime}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="time" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Line type="monotone" dataKey="user" stroke="#8884d8" name="User Service" />
                    <Line type="monotone" dataKey="order" stroke="#82ca9d" name="Order Service" />
                  </LineChart>
                </ResponsiveContainer>
              </CardContent>
            </Card>
          </Grid>

          {/* Anomalies */}
          <Grid item xs={12}>
            <Typography variant="h5" gutterBottom sx={{ mt: 3 }}>
              AI Anomaly Detection
            </Typography>
          </Grid>

          {anomalies.map((anomaly) => (
            <Grid item xs={12} md={6} key={anomaly.id}>
              <Card>
                <CardContent>
                  <Box display="flex" justifyContent="space-between" alignItems="center" mb={1}>
                    <Typography variant="h6">{anomaly.service}</Typography>
                    <Chip 
                      label={anomaly.type} 
                      color={getSeverityColor(anomaly.severity)}
                      variant="outlined"
                    />
                  </Box>
                  <Typography variant="body2" color="text.secondary" mb={1}>
                    {anomaly.description}
                  </Typography>
                  <Typography variant="caption" color="text.secondary">
                    {new Date(anomaly.timestamp).toLocaleString()}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
    </div>
  );
}

export default App;
